//! change bg
const heroRef=document.querySelector(".hero"),changeImgRef=document.querySelector(".header__btn-bg"),imgsArr=["../../img/bg1.jpg","../../img/bg2.jpg"];changeImgRef.addEventListener("click",(()=>{const e=imgsArr.length-1,r=Math.round(Math.random()*(e-0)+0),g=imgsArr[r];heroRef.style.backgroundImage=`url(${g})`}));